--
-- 表的结构 `model`
--

CREATE TABLE `model` (
  `id` int(10) UNSIGNED NOT NULL,
  `name` varchar(100) NOT NULL COMMENT '名称',
  `identity` varchar(250) NOT NULL COMMENT '唯一标识',
  `link` tinytext COMMENT '关联信息',
  `view_detail` tinytext COMMENT '模型视图',
  `view_edit` tinytext COMMENT '编辑视图',
